(835, 219, 'core_belief_1', 'We believe in the virgin birth of Jesus Christ and that there is no salvation in any other name.\r\n<ul>\r\n	<li>Matthew 1:20-22</li>\r\n	<li>Luke 1:26-27</li>\r\n	<li>Isaiah 7:14</li>\r\n	<li>Acts 4:11-12</li>\r\n	<li>Colossians 3:17</li>\r\n</ul>'),
(836, 219, '_core_belief_1', 'field_51e4774e02d1c'),
(837, 219, 'core_belief_2', 'We believe in the death, burial, resurrection, and ascension of Christ.\r\n<ul>\r\n	<li>I Corinthians 15:3-5</li>\r\n	<li>Acts 13:28-31</li>\r\n	<li>Romans 6:3-10, 8:11</li>\r\n	<li>Mark 16:18-19</li>\r\n	<li>Luke 24:50-51</li>\r\n	<li>Acts 1:9-11</li>\r\n</ul>'),
(838, 219, '_core_belief_2', 'field_51e477ec02d1d'),
(839, 219, 'core_belief_3', 'We believe in a born-again experience from the old life in Adam to the new life in Jesus Christ.\r\n<ul>\r\n	<li>John 3:3</li>\r\n	<li>1 Corinthians 15:22</li>\r\n</ul>'),
(840, 219, '_core_belief_3', 'field_51e4781602d1e'),
